import React, { useState, useEffect } from 'react';
import logo from './logo.jpg';
import './App.css';
import SearchBar from '../SearchBar/SearchBar';
import SearchResult from '../SearchResult/SearchResult';
import Playlist from '../Playlist/Playlist';
import Track from '../Track/Track';


function App() {

  // const [name, setName] = useState('Example Name');
  // const [artist, setArtist] = useState('Example Artist');
  // const [album, setAlbum] = useState('Example Album');
  // const [id, setId] = useState('Example ID');

  // const getSearchResults = () => {
  //   return (
  //     {name, artist, album, id}
  //   );
  // }

  const CLIENT_ID = 'da32ad9710d44c12ba2961fc6bda4dda';
  const REDIRECT_URI = "http://localhost:3000";
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
  const RESPONSE_TYPE = "token";

  const [searchResults, setSearchResults] = useState([
    { name: 'Rocket Man', artist: 'Elton John', album: 'Honky Château', id: 1 },
    { name: 'Space Oddity', artist: 'David Bowie', album: 'Space Oddity', id: 2 }
  ]);

  const [toPlaylist, setToPlaylist] = useState([]);
  useEffect(() => {
    if (!toPlaylist) {
      setToPlaylist([]);
    }})

  return (
    <div className="App">
      <h1>Jammming</h1>
      <div className="logo"></div>
      <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login to Spotify</a>
      <div className="App">
        <SearchBar />
        <div className="App-playlist">
          <SearchResult 
            searchResults={searchResults} 
            onAdd={tr => toPlaylist.some((i) => tr.searchResultObject === i) ? setToPlaylist(prev => { return prev.filter(newTr => newTr !== tr.searchResultObject)}) : setToPlaylist(prev => [tr.searchResultObject, ...prev]) } 
            toPlaylist={toPlaylist}
          />
          <Playlist 
            toPlaylist={toPlaylist}
            onAdd={tr => toPlaylist.some((i) => tr.searchResultObject === i) ? setToPlaylist(prev => { return prev.filter(newTr => newTr !== tr.searchResultObject)}) : setToPlaylist(prev => [tr.searchResultObject, ...prev]) } 
          />
        </div>
      </div>
    </div>
  );
}

export default App;
