import React, { useState } from 'react';
import './Track.css';

function Track(props) {

    const addToPlaylist = (e) => {
        props.onAdd(props);
    }
    console.log(props.toPlaylist);

    const toggleButton = (tr) => {
        props.toPlaylist.some((i) => tr.toPlaylist === i) ? 'Remove' : 'Add'
    }


    return(
        <div className="Track">
            <div className="Track-information">
                <h3>{props.searchResultObject.name}</h3>
                <p>{props.searchResultObject.artist} | {props.searchResultObject.album}</p>
            </div>
            <button className="buttonAddTrack" onClick={addToPlaylist}>
                {toggleButton(props.searchResultObject)}
            </button>
        </div>
    )
}

export default Track;
