import React, { useState } from "react";
import './SearchBar.css';

function SearchBar() {
    const [searchTerm, setSearchTerm] = useState("");

    const handleChange = (e) => {
        setSearchTerm(e.target.value);
    }

    const handleSubmit = () => {
        console.log(`load items: ${searchTerm}`)
    }

    return(
        <div className="SearchBar">
            <input placeholder="Enter a Song, Album, Artist..." onChange={handleChange}></input>
            <button className="SearchButton" onSubmit={handleSubmit}>SEARCH</button>
        </div>
    )
}

export default SearchBar;
